import { usersInfo } from "../data/usersInfo";

export { authorization };

const toolbar = {
  view: "toolbar",
  id: "toolbarAuthorization",
  padding: 3,
  elements: [
    {
      view: "label",
      label: "<span class='label_color'>Phone Shop</span>"
    },
    {},
    {
      view: "button",
      label: "Login",
      width: 90,
      click: function() {}
    },
    {
      view: "button",
      label: "Register",
      width: 150,
      click: function() {
        $$("windowRegister").show();
      }
    }
  ]
};

let authorization = {
  id: "authorization",
  rows: [toolbar]
};

webix.ui({
  view: "window",
  id: "windowRegister",
  modal: true,
  position: "center",
  label: {
    width: 140
  },
  head: {
    view: "toolbar",
    type: "clean",
    // css: "window_toolbar",
    cols: [
      { template: "Register", css: "window_toolbar_progress" },
      {
        view: "icon",
        icon: "mdi mdi-close",
        css: "alter",
        hotkey: "esc",
        click: function() {
          $$("windowRegister").hide();
        }
      }
    ]
  },
  body: {
    type: "clean",
    cols: [
      { width: 100 },
      {
        view: "form",
        id: "formInWindowRegister",
        width: 500,
        elementsConfig: {
          labelWidth: 150
        },
        elements: [
          {
            view: "text",
            label: "Name",
            type: "text",
            name: "name",
            attributes: {
              required: "true",
              title: "Enter your name",
              maxlength: 22
            }
          },
          {
            view: "text",
            type: "email",
            value: "newuser@gmail.com",
            label: "E-Mail Address",
            id: "email",
            name: "mail",
            attributes: {
              required: "true",
              title: "Enter your email"
            },
            invalidMessage: ""
          },
          {
            view: "text",
            id: "password",
            label: "Password",
            name: "password",
            attributes: {
              required: "true",
              title: "Create your password"
            },
            invalidMessage: "The password confirmation does not match"
          },
          {
            view: "text",
            id: "comfPassword",
            label: "Confirm Password",
            name: "confPassword",
            attributes: {
              required: "true",
              title: "repeat password"
            },
            invalidMessage: ""
          },
          {
            cols: [
              { width: 150 },
              {
                view: "button",
                value: "Register",
                css: "webix_primary",
                width: 150,
                click: function() {
                  let form = $$("formInWindowRegister");
                  if (form.validate()) {
                    // create userInsfo
                    const values = form.getValues();
                    console.log(values);
                    let newObj = {};

                    const arr = usersInfo.serialize();
                    const lastId = arr[arr.length - 1].userId;
                    const newId = lastId + 1;

                    newObj.userId = newId;
                    newObj.name = values.name;
                    newObj.email = values.mail;
                    newObj.password = values.password;

                    usersInfo.add(newObj, -1);
                    //
                    $$("windowRegister").hide();
                    $$("shopPage").show();
                  } else {
                    return;
                  }
                }
              },
              {}
            ]
          }
        ],
        rules: {
          name: webix.rules.isNotEmpty,
          mail: function(value) {
            let flag = 0;
            usersInfo.find(obj => {
              if (obj.email == value) {
                $$("email").config.invalidMessage =
                  "The email has already been taken";
                flag = 1;
              } else {
                $$("email").config.invalidMessage = "The email is not correct";
              }
            });
            return webix.rules.isEmail(value) && flag != 1;
          },
          password: function(value) {
            return (
              webix.rules.isNotEmpty(value) &&
              value != "" &&
              value == $$("comfPassword").getValue()
            );
          },
          confPassword: function(value) {
            let password = $$("password").getValue();
            if (value != password) {
              $$("comfPassword").config.value = "";
            }
            return webix.rules.isNotEmpty(value) && value == password;
          }
        }
      },
      { width: 100 }
    ]
  }
});
